package org.leanpoker.player.domain;import org.leanpoker.player.domain.model.Card;import java.util.List;public class PostFlopBiddingStrategy {    private final CardScorer scorer;    public PostFlopBiddingStrategy(CardScorer scorer) {        this.scorer = scorer;    }    public int decideBid(List<Card> cards, List<Card> communityCards, int ourMoney, int buyIn) {        int overallScore = scorer.overallScore(cards);        if (overallScore >= 12) {            return ourMoney;        } if (overallScore > 10) {            return Math.max(buyIn, 10);        } else {            return 0;        }    }}